import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { create as createUser } from "../../services/users";
import Cookies from "universal-cookie";
import logo from "../../assets/logo200.png";
import Modal from "../../components/Modal";
import styled from "styled-components";

// Toastify
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

//Components
import Input from "../../../src/components/Input/index";

//CSS
import "./Home.css";
import "./Signup.css";

export default function Home() {
  const navigate = useNavigate();
  const cookies = new Cookies();
  const userLogged = cookies.get("Usuario");
  console.log(userLogged);

  const logout = () => {
    toast.success("Gracias por tu visita vuelve pronto!!");
    cookies.remove("Usuario", { path: "/" });
    cookies.remove("TipoUsuario", { path: "/" });
    cookies.remove("NombreResturante", { path: "/" });
    navigate("/");
  };
  // Local state
  const [username, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [restaurants, setRestaurants] = useState("");
  const [password, setPassword] = useState("");
  const [userType, setUserType] = useState("");
  const [itemActive, setItemActive] = useState(null);
  const [itemActiveRegister, setItemActiveRegister] = useState("NoRegister");
  const [estadoModal1, cambiarEstadoModal1] = useState(false);

  // This is to active input restaurant
  const isActive = (itemTypeUser) => itemTypeUser === itemActive;
  //This active register form
  const isActiveRegister = (itemRegister) =>
    itemRegister === itemActiveRegister;

  const cleanForm = () => {
    setUserName("");
    setEmail("");
    setRestaurants("");
    setPassword("");
    setUserType("");
  };

  const isEmpty = (value) => !value;

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (
      isEmpty(username) ||
      isEmpty(email) ||
      isEmpty(password) ||
      isEmpty(userType)
    ) {
      toast.error("Se ingresaron datos incorrectos!!!!");
      return;
    }

    const data = {
      username,
      email,
      password,
      restaurants,
      userType,
    };

    try {
      await createUser(data);
      toast.success("Registro exitoso!!");
      cleanForm();
      navigate("/login");
    } catch (error) {
      console.error(error);
    }
  };

  const Boton = styled.button`
    display: block;
    padding: 10px 30px;
    border-radius: 100px;
    color: #fff;
    border: none;
    background: #1766dc;
    cursor: pointer;
    font-family: "Roboto", sans-serif;
    font-weight: 500;
    transition: 0.3s ease all;
    &:hover {
      background: #0066ff;
    }
  `;

  const Contenido = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    h1 {
      font-size: 42px;
      font-weight: 700;
      margin-bottom: 10px;
    }
    p {
      font-size: 18px;
      margin-bottom: 20px;
    }
    img {
      width: 100%;
      vertical-align: top;
      border-radius: 3px;
    }
  `;

  return (
    <div className="m-1 d-flex flex-row justify-content-around align-items-center ">
      <img
        src="https://www.lifeder.com/wp-content/uploads/2018/04/Nuestra-comida-deberia-ser-nuestra-medicina-y-nuestra-medicina-deberia-ser-nuestra-comida.-min.jpg"
        alt="cabecera"
        className="img-home"
      ></img>
      <div
        className={`${
          isActiveRegister("NoRegister")
            ? "row justify-content-center container active"
            : "row justify-content-center container d-none"
        }`}
      >
        <img className="img-home" src={logo} alt="logo" width="50px" />
        <h3 className="p-home">
          Esta pagina busca mejorar tu experiencia digital de consulta de
          menus.Te invitamos a registrarte o ir directamente a ver los menu
        </h3>

        <button
          type="button"
          className={`${userLogged ? "btn-home active" : "btn-home d-none"}`}
          onClick={logout}
        >
          Cerrar Sesion
        </button>

        <button
          onClick={() => cambiarEstadoModal1(!estadoModal1)}
          type="button"
          className={`${!userLogged ? "btn-home active" : "btn-home d-none"}`}
        >
          Inicion Sesion
        </button>

        <button
          type="button"
          className="btn-home"
          value="Register"
          onClick={(e) => setItemActiveRegister(e.target.value)}
        >
          Registrarme
        </button>
        <Modal
          estado={estadoModal1}
          cambiarEstado={cambiarEstadoModal1}
          titulo="Hola!"
          mostrarHeader={true}
          mostrarOverlay={true}
          posicionModal={"center"}
          padding={"20px"}
        >
          <Contenido>
            <h1>Ventana Modal</h1>
            <p>Reutilizable y con opciones de personalización.</p>
            <Boton onClick={() => cambiarEstadoModal1(!estadoModal1)}>
              Aceptar
            </Boton>
          </Contenido>
        </Modal>
      </div>
      <div
        className={`${
          isActiveRegister("Register") ? "container active" : "container d-none"
        }`}
      >
        <div className="col-12 col-md-12  ">
          <div className="title-registro">
            <h2 className="card-title text-center">Registro</h2>
          </div>
          <div className="card-body py-md-4 form-select form-select-mg">
            <form className="" onSubmit={handleSubmit}>
              <div className="form-group">
                <div className="select-child">
                  <select
                    type="text"
                    className="form-select form-select-mg2"
                    placeholder=""
                    value={userType}
                    onChange={(e) => setUserType(e.target.value)}
                    onClick={(e) => setItemActive(e.target.value)}
                  >
                    <option value="Select">
                      Selecciona tu tipo de usuario
                    </option>
                    <option value="Comensal">Comensal</option>
                    <option value="Administrador de restaurante">
                      Administrador de restaurante
                    </option>
                  </select>
                </div>
                <Input
                  type="text"
                  className="form-control"
                  placeholder="Nombre"
                  id="meal"
                  name="meal"
                  value={username}
                  callback={(e) => setUserName(e.target.value)}
                />
              </div>
              <div className="form-group">
                <Input
                  type="email"
                  className="form-control"
                  placeholder="Correo"
                  id="meal"
                  name="meal"
                  value={email}
                  callback={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="form-group">
                <Input
                  type="text"
                  className={`${
                    isActive("Administrador de restaurante")
                      ? "form-control active"
                      : "form-control d-none"
                  }`}
                  placeholder="Nombre del Restaurante"
                  id="meal"
                  name="meal"
                  value={restaurants}
                  callback={(e) => setRestaurants(e.target.value)}
                />
              </div>

              <div className="form-group">
                <Input
                  type="password"
                  className="form-control"
                  placeholder="Contraseña"
                  id="meal"
                  name="meal"
                  value={password}
                  callback={(e) => setPassword(e.target.value)}
                />
              </div>
              <div className="d-flex flex-row align-items-center justify-content-between">
                <button type="submit" className="btn-signup">
                  Crear Cuenta
                </button>
              </div>
            </form>
            <button
              href="/login"
              className="btn-signup"
              onClick={(e) => setItemActiveRegister(e.target.value)}
              value="NoRegister"
            >
              Home
            </button>
          </div>
        </div>
      </div>
      <ToastContainer />
    </div>
  );
}
