import { useState } from "react";
import { login as loginUser } from "../../services/menus";

// Toastify
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Input from "../../../src/components/Input/index";

import "./Login.css";

export default function Login() {
  // Local state
  const [userName, setUserName] = useState("");
  const [userPassword, setUserPassword] = useState("");

  const cleanForm = () => {
    setUserName("");
    setUserPassword("");
  };

  const isEmpty = (value) => !value;

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (isEmpty(userName) || isEmpty(userPassword)) {
      toast.error("Se ingresaron datos incorrectos!!!!");
      return;
    }

    const data = {
      userName,
      userPassword,
    };

    try {
      await loginUser(data);
      toast.success("Gracias, se registro con exito!!");
      cleanForm();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container-login">
      <div className="form-login">
        <form onSubmit={handleSubmit}>
          <h5>Formulario Login</h5>
          <Input
            type="text"
            className="controls"
            placeholder="Usuario"
            id="meal"
            name="meal"
            value={userName || ""}
            callback={(e) => setUserName(e.target.value)}
          />
          <Input
            type="password"
            className="controls"
            placeholder="Password"
            id="meal"
            name="meal"
            value={userPassword || ""}
            callback={(e) => setUserPassword(e.target.value)}
          />
          <button type="submit" className="btn-7 button_form">
            Ingresar
          </button>
          <p className="register">Registrarte</p>
          <p className="forget-password">¿Olvidastes tu Contraseña?</p>
        </form>
      </div>
      <ToastContainer />
    </div>
  );
}
