import { useEffect, useState } from "react";
import { sublistRestaurant as listDishes } from "../../../src/services/menus";
import { useParams, useNavigate } from "react-router-dom";

//components
import MenuCard from "../../components/MenuCard";
import Delete from "../Menu/Delete/index";

//Cookies
import Cookies from "universal-cookie";

//Modal
import Modal from "react-modal";

import "./Menu.css";

export default function Menu() {
  //Styles modal
  const customStyles = {
    content: {
      top: "30%",
      left: "50%",
      right: "40%",
      bottom: "20%",
      marginRight: "-50%",
      transform: "translate(-50%, -50%)",
    },
  };
  Modal.setAppElement("#root");

  // Local state
  const [dishes, setDishes] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  // Querys
  const { restaurantName } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const list = async () => {
      const data = await listDishes(restaurantName);
      const parsedDishes = Object.keys(data).map((key) => {
        return { id: key, ...data[key] };
      });

      setDishes(parsedDishes);
    };

    list();
  }, []);

  const cookies = new Cookies();
  cookies.set("EndpointRestaurant", restaurantName, { path: "/" });

  return (
    <div className="mainContainer">
      <div className="container">
        <div className="row">
          <div className="container-btn">
            <button
              className="btn-form"
              onClick={() => navigate(`/formulario`)}
            >
              Ir a registrar platillos
            </button>
          </div>
          {dishes &&
            dishes.map((dish) => <MenuCard dish={dish} navigate={navigate} />)}
          <div
            className="icons8-trash"
            onClick={() => setModalIsOpen(true)}
          ></div>
        </div>

        <div className="info">
          <p>LA PROPINA NO ES OBLIGATORIA.</p>
          <p>
            ACEPTAMOS PAGOS EN EFECTIVO, TARJETAS VISA, MASTER CARD Y AMERICAN
            EXPRESS.
          </p>

          <p>EL PAGO CON TARJETA NO GENERA NINGUNA COMISIÃ“N.</p>
        </div>
        {/* <div className="qrcontainer">
              <QrCode />
        </div> */}
        <Modal isOpen={modalIsOpen} style={customStyles}>
          <Delete />
          <button className="btn-singup" onClick={() => setModalIsOpen(false)}>
            Con esto me cierro
          </button>
        </Modal>
      </div>
    </div>
  );
}
