import { useEffect, useState } from "react";
import { list as listDishes } from "../../../src/services/menus";
import { useParams, useNavigate } from "react-router-dom";
import MenuCard from "../../components/MenuCard";

import "./Menu.css";

export default function Menu() {
  // Local state
  const [dishes, setDishes] = useState([]);

  // RRD
  const navigate = useNavigate();
  const { category, restaurantName } = useParams();
  console.log(category, restaurantName);

  useEffect(() => {
    const list = async () => {
      const data = await listDishes("Cortes", restaurantName);
      const parsedDishes = Object.keys(data).map((key) => {
        return { id: key, ...data[key] };
      });

      setDishes(parsedDishes);
    };

    list();
  }, []);

  return (
    <div className="mainContainer">
      <div className="container">
        <div className="row">
          <div className="container-btn">
            <button
              className="btn-form"
              onClick={() => navigate(`/formulario`)}
            >
              Ir a registrar platillos
            </button>
          </div>
          {dishes &&
            dishes.map((dish) => <MenuCard dish={dish} navigate={navigate} />)}
        </div>

        <div className="info">
          <p>LA PROPINA NO ES OBLIGATORIA.</p>
          <p>
            ACEPTAMOS PAGOS EN EFECTIVO, TARJETAS VISA, MASTER CARD Y AMERICAN
            EXPRESS.
          </p>

          <p>EL PAGO CON TARJETA NO GENERA NINGUNA COMISIÃ“N.</p>
        </div>
        {/* <div className="qrcontainer">
              <QrCode />
        </div> */}
      </div>
    </div>
  );
}
