import { useEffect, useState } from "react";
import { sublistRestaurant as listDishes } from "../../../src/services/menus";
import { useParams, useNavigate } from "react-router-dom";

//components
import MenuCard from "../../components/MenuCard";

//Cookies
import Cookies from "universal-cookie";

//Modal
import Modal from "react-modal";

import "./Menu.css";

export default function Menu() {
  // Local state
  const [dishes, setDishes] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  // Querys
  const { restaurantName } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const list = async () => {
      const data = await listDishes(restaurantName);
      const parsedDishes = Object.keys(data).map((key) => {
        return { id: key, ...data[key] };
      });

      setDishes(parsedDishes);
    };

    list();
  }, []);

  const cookies = new Cookies();
  cookies.set("EndpointRestaurant", restaurantName, { path: "/" });

  return (
    <div className="mainContainer">
      <div className="container">
        <div className="row">
          <div className="container-btn">
            <button
              className="btn-form"
              onClick={() => navigate(`/formulario`)}
            >
              Ir a registrar platillos
            </button>
          </div>
          {dishes &&
            dishes.map((dish) => <MenuCard dish={dish} navigate={navigate} />)}
        </div>
        <div className="info">
          <p>LA PROPINA NO ES OBLIGATORIA.</p>
          <p>
            ACEPTAMOS PAGOS EN EFECTIVO, TARJETAS VISA, MASTER CARD Y AMERICAN
            EXPRESS.
          </p>

          <p>EL PAGO CON TARJETA NO GENERA NINGUNA COMISIÓN.</p>
        </div>
        {/* <div className="qrcontainer">
              <QrCode />
        </div> */}
        <Modal isOpen={modalIsOpen} style={customStyles}>
          <div className="col  col-6" key={dish._id}>
            <div className="food-menu">
              <div>
                <div className="food">
                  <div className="food-col food-image col-6">
                    <img src={dish.image_Url} alt="Food" />
                  </div>

                  <div className="name-food-name">
                    <div className="food-col food-detail">
                      <h5>{dish.dishName}</h5>
                      <span className="name-food-price"> ${dish.price}</span>
                    </div>

                    <div className="name-food-descrip">{descripcion}</div>
                  </div>
                  <div className="iconos">
                    <div
                      className="icons8-comments"
                      onClick={() => navigate(`/detalle/${dish._id}`)}
                    ></div>
                    <div
                      className="icons8-edit"
                      onClick={() => navigate(`edit/${dish._id}`)}
                    ></div>
                    <div
                      className="icons8-trash"
                      onClick={() => setModalIsOpen(true)}
                    ></div>

                    <button
                      className="btn-singup"
                      onClick={() => setModalIsOpen(false)}
                    >
                      Con esto me cierro
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </Modal>
      </div>
    </div>
  );
}
