import { useState } from "react";
import { create as createMenu } from "../../services/menus";

// Toastify
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Input from "../../../src/components/Input/index";

import "./Login.css";

export default function Login() {
  // Local state
  const [dishName, setDishName] = useState("");
  const [category, setCategory] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");

  const cleanForm = () => {
    setDishName("");
    setCategory("");
    setDescription("");
    setPrice("");
  };

  const isEmpty = (value) => !value;

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (
      isEmpty(dishName) ||
      isEmpty(category) ||
      isEmpty(description) ||
      isEmpty(price)
    ) {
      toast.error("Favor de llenar la forma completa!!!!");
      return;
    }

    const data = {
      dishName,
      category,
      description,
      price,
    };

    try {
      await createMenu(data);
      toast.success("Gracias, se registro con exito!!");
      cleanForm();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container-login">
      <div className="form-login">
        <h5>Formulario Login</h5>
        <Input
          type="text"
          className="controls"
          placeholder="Usuario"
          id="meal"
          name="meal"
          value={dishName || ""}
          callback={(e) => setDishName(e.target.value)}
        />
        <Input
          type="password"
          className="controls"
          placeholder="Password"
          id="meal"
          name="meal"
          value={dishName || ""}
          callback={(e) => setDishName(e.target.value)}
        />
        <button type="submit" className="btn-7 button_form">
          Ingresar
        </button>
        <p className="register">Registrarte</p>
        <p className="forget-password">¿Olvidastes tu Contraseña?</p>
      </div>
      <ToastContainer />
    </div>
  );
}
