const URL = "http://localhost:8000";
//const URL = "https://menus.api.nesjes.com";

const generateConfig = (body) => {
  return {
    method: "POST",
    headers: {
      "Content-Type": "Application/JSON",
    },
    body: JSON.stringify(body),
  };
};

export const createAdmin = async (body) => {
  const requestConfig = generateConfig(body);
  const response = await fetch(`${URL}/admins`, requestConfig);
  const data = await response.json();
  return data;
};

export const login = async (body) => {
  const requestConfig = generateConfig(body);
  const response = await fetch(`${URL}/admins/login`, requestConfig);
  const data = await response.json();
  return data;
};

export const retrieve = async (id) => {
  const response = await fetch(`${URL}/admins/edit?_id=${id}`);
  const data = await response.json();
  return data;
};

export const list = async () => {
  const response = await fetch(`${URL}/admins`);
  const data = await response.json();
  return data;
};

export const sublist = async (restaurant) => {
  const response = await fetch(
    `${URL}/admins/restaurant?restaurants=${restaurant}`
  );
  const data = await response.json();
  return data;
};

export const update = async (id, body) => {
  const response = await fetch(`${URL}/admins/edit?_id=${id}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "Application/JSON",
    },
    body: JSON.stringify(body),
  });
  const data = await response.json();
  return data;
};

export const deleteAdmin = async (id, body) => {
  const response = await fetch(`${URL}/admins/${id}`, {
    method: "DELETE",
    headers: {
      "Content-Type": "Application/JSON",
    },
    body: JSON.stringify(body),
  });
  const data = await response.json();
  return data;
};
